import React, { Component } from "react";
import { Grid } from "@material-ui/core";
import Table from "./table.jsx";
import Form from "./form.jsx";
import Analysis from "../Analysis/Analysis";
import Graph from "./graph";

export class Asset extends Component {
  constructor(props) {
    super(props);
    this.state = {
      analysisOpen: false,
      assetOpen: true,
      assetName: "",
      interestRate: 0,
      currentValue: 0,
    };
    this.changeState = this.changeState.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  createData(name, interest, amount) {
    return { name, interest, amount };
  }

  changeState(newState) {
    this.setState({ analysisOpen: newState, assetOpen: !newState });
  }

  handleChange(name, interest, value) {
    this.setState({
      assetName: name,
      interestRate: interest,
      currentValue: value,
    });
    console.log(
      this.state.assetName,
      this.state.interestRate,
      this.state.currentValue
    );
  }
  render() {
    const name = this.props.name;

    if (this.state.analysisOpen) {
      return <Analysis name={name} />;
    }

    return (
      <Grid container direction="column">
        <Graph />
        <br />
        <br />
        <Grid item container>
          <Grid item sm={2}></Grid>
          <Grid item sm={8} xs={12}>
            <Form
              asset={this.state.assetName}
              interest={this.state.interestRate}
              amount={this.state.currentValue}
              onChange={this.handleChange}
            />
          </Grid>
          <Grid item sm={2}></Grid>
          <br />
          <br />
          <Grid item sm={1}></Grid>
          <Grid item sm={10} xs={12}>
            <Table
              asset="ocbc"
              interest="4"
              amount="5"
            />
          </Grid>
          <Grid item sm={1}></Grid>
        </Grid>
      </Grid>
    );
  }
}
