import React from "react";
import TextField from "@material-ui/core/TextField";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "25ch",
    },
  },
}));

export default function FormPropsTextFields(props) {
  const classes = useStyles();
  const [state, setState] = React.useState({
    assetName: "h",
    interest: 0,
    value: 0,
    data: [],
  });

  const handleNameChange = (e) => {
    setState({ assetName: e.target.value });
  };

  const handleInterestChange = (e) => {
    setState({ interest: e.target.value });
  };

  const handleValueChange = (e) => {
    setState({ value: e.target.value });
  };

  const addAsset = (e) => {
    const name = state.assetName;
    const interest = state.interest;
    const value = state.value;
    props.onChange(name, interest, value);
    // props.onChange(newAsset);
    //const newList = [...data, newAsset];
    // setState({
    //   data: [...state.data, { newAsset }],
    // });
   };

  return (
    <form className={classes.root} autoComplete="off">
      <div>
        <TextField
          id="asset_name"
          label="Asset Name"
          variant="outlined"
          //value={state.assetName}
          onChange={handleNameChange}
        />

        <TextField
          id="interest_rate"
          label="Asset Interest Rate"
          type="number"
          variant="outlined"
          //value={state.interest}
          step={0.1}
          InputProps={{ inputProps: { min: 0, step: 0.1 } }}
          onChange={handleInterestChange}
        />
        <TextField
          id="current_value"
          label="Current Value"
          type="number"
          variant="outlined"
          //value={state.value}
          InputProps={{ inputProps: { min: 0, step: 1000 } }}          
          onChange={handleValueChange}
        />
        <Button variant="contained" color="primary" onClick={addAsset} >
          Add this asset
        </Button>
      </div>
    </form>
  );
}
